INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('8', '4', '新用户注册', '<div class=\"helpcon\">\r\n<p style=\"line-height: 25px\">欢迎光临和茶网（hecha.cn），和茶网注册快速简洁。目前有有两种途径注册：网站注册；电话注册。</p>\r\n<p style=\"line-height: 25px\">　　<br />\r\n<strong>1.网站注册</strong><br />\r\n　(1)进入和茶网（hecha.cn）后，点击页面右上方&ldquo;登录/注册&rdquo;</p>\r\n<p style=\"line-height: 25px\"><img alt=\"\" src=\"http://www.hecha.cn/images/upload/Image/11(4).jpg\" width=\"254\" height=\"95\" /></p>\r\n<p style=\"line-height: 25px\">　将出现&ldquo;登录/注册&rdquo;页面，点击&ldquo;新用户注册&rdquo;，在新页面相应提示处输入您的邮箱地址（会自动默认注册用户名等同于邮箱地址）、密码（密码设置不要过于简单）、确认密码后输入验证码，点击&ldquo;完成&rdquo;即可，注册用户名是唯一的。注册成功后，您可以到网站&ldquo;我的帐户-个人信息管理&rdquo;进行个人信息的完善和更新。</p>\r\n<p style=\"line-height: 25px\">&nbsp;</p>\r\n<p style=\"line-height: 25px\">　(2)在订购过程中注册</p>\r\n<p style=\"line-height: 25px\">　进入和茶网 &mdash;&mdash;选择您所需要的商品 &mdash;&mdash;点击&ldquo;我要收藏&rdquo;或&ldquo;立刻购买&rdquo;按钮&mdash;&mdash;弹出提示框提醒您登录&mdash;&mdash;点击确定，出现登录/注册页面 &mdash;&mdash;在页面点击&ldquo;注册&rdquo;进入注册页面。</p>\r\n<p style=\"line-height: 25px\">　　<br />\r\n<span style=\"color: #0000ff\"><u>点击这里注册新用户&gt;&gt;</u></span></p>\r\n<p style=\"line-height: 25px\">　　<br />\r\n<strong>2.电话订购注册</strong><br />\r\n　在您致电和茶网导购中心400-8899-379订购产品过程中可以通过导购人员为您注册。</p>\r\n</div>', '', '', '', '0', '1', '1367804291', '', '0', 'http://', '');
