INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('23', '10', '喝苦丁茶为什么有助于减肥', '<p>　　吃的苦中苦方为人上人。想要减肥利于不败之地。那非苦莫属。喝茶减肥首选苦丁，苦丁茶减肥功效作用如人皆知。虽说口感不是很好，但是功效不容小觑。不仅在降三高、清热解毒抗辐射等保健上效果好，其减肥功效依然不逊色。苦丁茶的减肥原理究竟是什么呢?</p>\r\n<p style=\"text-align: center\"><img alt=\"\" width=\"116\" height=\"106\" src=\"http://hcseo.hecha.cn/allimg/130428/2-13042Q0091GH.jpg\" /></p>\r\n<p>　　苦丁茶以苦出名。主要功效是帮助人们清除体内的垃圾毒素。苦丁茶含有的清除垃圾元素能消除人体内多余的油脂。以此达到减肥瘦身，不留多余赘肉的功效。尤其先生夏天是最适合饮苦丁茶的。</p>\r\n<p>　　<b>友情提醒</b>：若是受不了苦丁茶的苦味，可以加点少量蜂蜜。</p>\r\n<p>版权：本文( http://jk.hecha.cn/info/2013/show_17866.html )由和茶网原创转载请注明。</p>', '', '', '', '0', '1', '1367814840', 'data/article/1367815862290487929.jpg', '2', 'http://', '吃的苦中苦方为人上人。想要减肥利于不败之地。那非苦莫属。喝茶减肥首选苦丁，苦丁茶减肥功效作用如人皆知。虽说口感不是很好，但是功效不容小觑。不仅在降三高、清热解毒抗辐射等保健上效果好，其减肥功效依然不逊色。苦丁茶的减肥原理究竟是什么呢?');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('24', '10', '白癜风患者不宜喝绿茶', '<p>　　绿茶在所有茶类中是个异类。绿茶是一种未经过发酵的茶叶。这样的茶叶就算用水冲泡后依然含有少量的维生素C，而维生素C元素对于治疗白癜风病症是非常不利的。所以白癜风的患者就好不要饮用绿茶。尽量少饮，以避免影响病情的康复。</p>\r\n<p style=\"text-align: center\"><img alt=\"\" src=\"http://hcseo.hecha.cn/allimg/130428/2-13042P9522L53.jpg\" style=\"width: 130px; height: 120px\" /></p>\r\n<p>　　白癜风患者可以饮茶，但是最好不要饮绿茶。还不能饮浓茶，以淡茶为主。其实选择其他的茶叶以少量淡饮的原则。适当的喝的茶水是对于防治白癜风并且还是有利的。</p>\r\n<p>　　<b>友情提醒</b>：白癜风患者应该多吃些五谷杂粮。尤其是热食物，不宜吃菠菜。</p>\r\n<p>版权：本文( http://jk.hecha.cn/info/2013/show_17864.html )由和茶网原创转载请注明。</p>', '', '', '', '0', '1', '1367816173', '', '0', 'http://', '');
INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('25', '10', '便秘的人不宜喝浓茶', '<p>　　长期就坐，以是现在都市生活一种方式。这样长久下来会容易产生便秘。要是便秘了怎么办?有人说便秘了用茶来解决是不错的选择。便秘喝浓茶能缓解其实这是一个错误的认识，便秘喝茶不仅不会及时得以缓解，而且有可能加重便秘的情况。</p>\r\n<p style=\"text-align: center\"><img alt=\"\" src=\"http://hcseo.hecha.cn/allimg/130427/2-13042G0155A61.jpg\" width=\"133\" height=\"98\" /></p>\r\n<p>　　这是因为茶叶中的茶多酚类的一些元素物质能够对肠胃黏膜起到收敛的作用。这样不仅会影响肠道为食物的消化，还能导致大便干结诱发便秘甚至加重便秘症状。所以便秘患者应该尽量不喝茶，尤其是喝浓茶。</p>\r\n<p>　　<b>友情提醒</b>：多喝白开水多运动增强改善肠胃环境。同时还要多吃些水果(如，香蕉)和粗粮。</p>\r\n<p>版权：本文( http://jk.hecha.cn/info/2013/show_17861.html )由和茶网原创转载请注明。</p>', '', '', '', '0', '1', '1367816236', '', '0', 'http://', '');
