INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('38', '10', '信阳毛尖的泡法与饮用_信', '<p>　　今日茶话，又是新的一周，今天和茶网小编给大家介绍的是一种叫马黛茶的功效和作用。马黛茶可能很多茶友都不是很了解。或许还是第一次听到此茶，马黛茶又名耶稣会茶主要生产于南美洲的一种茶叶。成品的马黛茶叶外形碎末状，喝起来有点苦涩。其功效作用可非一般。</p>\r\n<p style=\"text-align: center\"><img alt=\"\" src=\"http://hcseo.hecha.cn/allimg/130520/2-130520105032195.jpg\" width=\"152\" height=\"119\" /></p>\r\n<p>　　此茶也有四百多年的历史。马黛茶最神奇的就是能够让人体的新陈代谢自然的加速循环。这也是许多明星们经常用来保持体形的一种方式哦。其因马黛茶中含有许多不饱和脂肪酸，能够对人体的血压调节及血脂的降低都有很显著的功效作用。所以也是许多喜欢食荤人的最佳伴侣。</p>\r\n<p>　　<b>和茶网提醒</b>：马黛茶对于过敏症患者来说是一个最好的福利。马黛茶能够让人摆脱过敏症的烦恼。</p>\r\n<p>版权：本文( http://jk.hecha.cn/info/2013/show_17932.html )由和茶网原创转载请注明。</p>', '', '', '', '0', '1', '1367817097', '', '0', 'http://', '');
